import React, { useState } from 'react'

function Counter() {
  const [count,setCount] = useState(0)
  const [count2,decrementCount] = useState(0)
  function handleClick(){
    setCount(count+1)
  }
  function decrementClick(){
    decrementCount(count2 - 1)
  }

  return (
    <div>
      <button onClick={handleClick}>Press me to {count} times</button>
      <button onClick={decrementClick}>Press me to {count} times to decrement</button>
    </div>
  )
}

export default Counter
