import React, { useState } from 'react'

function Counter() {
  const [count,setCount] = useState(0)
  function handleClick(){
    setCount(count+1)
  }

  return (
    <div>
      <button onClick={handleClick}>Press me to {count} times</button>
      <button onClick={handleClick}>Press me to {count} times to decr</button>
    </div>
  )
}

export default Counter

